mapper.select:
select s.*,c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%',#{name},'%')
and s.status = #{status}
and s.category_id = #{categoryId}
order by s.create_time desc

mapper.update:
update setmeal set name = #{name}, category_id = #{categoryId}, price = #{price},
status = #{status}, create_time = #{createTime}, update_time = #{updateTime},
create_user = #{createUser}, update_user = #{updateUser}, image = #{image},
description = #{description}
where id = #{id}

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
